mapper.update:

UPDATE MEMBER_TABLE
SET MEM_PWD = #{mem_pwd}, MEM_GENDER = #{mem_gender},
    MEM_PHONE = #{mem_phone}, MEM_BIRTH = #{mem_birth},
    MEM_ADDRESS = #{mem_address}, MEM_TYPE = #{mem_type},
    MEM_NICKNAME = #{mem_nickname}, MEM_NAME = #{mem_name}
WHERE MEM_ID = #{mem_id}


UPDATE BOARD_TABLE
SET POST_CONTENT = #{post_content}, POST_TITLE = #{post_title}
WHERE POST_NO = #{post_no}

mapper.select:

SELECT NVL(MAX(CLAP), 0)
FROM CLAP_TABLE
WHERE MEM_ID = #{mem_id} AND POST_NO = #{post_no}

mapper.update:

UPDATE CLAP_TABLE
SET CLAP = CLAP + 1
WHERE POST_NO = #{post_no} AND MEM_ID = #{mem_id}

mapper.select:

SELECT TOTAL_CLAP
FROM BOARD_TABLE
WHERE POST_NO = #{post_no}

mapper.update:

UPDATE COMMENT_TABLE
SET USE = 'N'
WHERE POST_NO=#{post_no} AND COM_NO=#{com_no}
